import React from 'react';
import '../styles/Photo.css';
//import { useState } from 'react';

const Photo = () =>{

   // const [inputPhoto, setInputPhoto] = useState("");

    onchange = e => {
        let files = e.target.files;
        console.log(files);
        let reader = new FileReader();
        reader.onload = r => {
             console.log(r.target.result);
             document.getElementById("img").src = r.target.result
             //setInputPhoto(r.target.result);
         };
        reader.readAsDataURL(files[0]);
       // localStorage.setItem('image', inputPhoto);
        //alert('se guardó tu foto en localstorage');
    }

    return (
    <div className="container">
        <div onSubmit={this.onFormSubmit}>
        <img className="home__img" id="img" alt="foto de perfil"/>
        <label className="Subirfoto" for="file">Subir foto</label>
        <input type="file" accept="image/png,image/jpeg" id="file" name="file" onchange={e => this.onchange(e)} />
      </div>
    </div>
    )
  }
export default Photo;
